import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';

// Login 컴포넌트
function Login(props) {
  const navigate = useNavigate();
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');

  return (
    <>
      <h2>로그인</h2>
      <div className="form">
        <p>
          <input
            className="login"
            type="text"
            name="username"
            placeholder="아이디"
            onChange={(event) => setId(event.target.value)}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            name="pwd"
            placeholder="비밀번호"
            onChange={(event) => setPassword(event.target.value)}
          />
        </p>
        <p>
          <input
            className="btn"
            type="submit"
            value="로그인"
            onClick={() => {
              const userData = { userId: id, userPassword: password };
              fetch('http://localhost:3001/login', {
                method: 'post',
                headers: { 'content-type': 'application/json' },
                body: JSON.stringify(userData),
              })
                .then((res) => res.json())
                .then((json) => {
                  if (json.isLogin === 'True') {
                    window.location.href = '/main.html';
                  } else {
                    alert(json.isLogin);
                  }
                });
            }}
          />
        </p>
      </div>
      <p>
        계정이 없으신가요?{' '}
        <button onClick={() => navigate('/signin')}>회원가입</button>
      </p>
    </>
  );
}

// Signin 컴포넌트
function Signin(props) {
  const navigate = useNavigate();
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');

  return (
    <>
      <h2>회원가입</h2>
      <div className="form">
        <p>
          <input
            className="login"
            type="text"
            placeholder="아이디"
            onChange={(event) => setId(event.target.value)}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            placeholder="비밀번호"
            onChange={(event) => setPassword(event.target.value)}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            placeholder="비밀번호 확인"
            onChange={(event) => setPassword2(event.target.value)}
          />
        </p>
        <p>
          <input
            className="btn"
            type="submit"
            value="회원가입"
            onClick={() => {
              const userData = {
                userId: id,
                userPassword: password,
                userPassword2: password2,
              };
              fetch('http://localhost:3001/signin', {
                method: 'post',
                headers: { 'content-type': 'application/json' },
                body: JSON.stringify(userData),
              })
                .then((res) => res.json())
                .then((json) => {
                  if (json.isSuccess === 'True') {
                    alert('회원가입이 완료되었습니다!');
                    navigate('/');
                  } else {
                    alert(json.isSuccess);
                  }
                });
            }}
          />
        </p>
      </div>
      <p>
        로그인 화면으로 돌아가기{' '}
        <button onClick={() => navigate('/')}>로그인</button>
      </p>
    </>
  );
}

// App 컴포넌트
function App() {
  const [mode, setMode] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/authcheck')
      .then((res) => res.json())
      .then((json) => {
        if (json.isLogin === 'True') {
          window.location.href = '/main.html';
        } else {
          setMode('LOGIN');
        }
      });
  }, []);

  return (
    <Router>
      <div className="background">
        <Routes>
          <Route path="/signin" element={<Signin setMode={setMode} />} />
          <Route path="/" element={<Login setMode={setMode} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
